import React, { useState,useRef, useEffect } from 'react'
import { useCart, useDispatchCart } from './ConextReducer';

const Card = (props) => {

let dispatch=useDispatchCart();
let data=useCart();
const priceRef=useRef();
let options=props.options;
let priceOptions=Object.keys(options);
const [qty,setQty]=useState(1);
const [size,setSize]=useState("")
const handaleAddToCart=async()=>{
  await dispatch({
    type:"ADD",
    id:props.foodItem._id,
    name:props.foodItem.name,
    price:props.finalPrice,
    qty:qty,size:size
  })
  console.log(data);
}
let finalPrice=qty*parseInt(options[size]);
useEffect(()=>{
  setSize(priceRef.current.value)
})
    return (
        <div>
            <div className="card mt-3 " style={{ width: '18rem', "maxHeight": '360px' }}>
                <img src={props.foodItem.img} className="card-img-top" alt="..." style={{height:"150px",objectFit:"fill"}} />
                <div className="card-body">
                    <h5 className="card-title" style={{ color: "black" }}>{props.foodItem.name}</h5>
                    <div className='container w-100'>
                        <select className='m-2 h-100  bg-success rounded' style={{ color: "white" }} onChange={(e)=> setQty(e.target.value)}>
                            {Array.from(Array(6), (e, i) => {
                                return (
                                    <option key={i + 1} value={i + 1}>{i + 1}</option>
                                )
                            })}
                        </select>

                        <select className='m-2 h-100  bg-success rounded' ref={priceRef} style={{ color: "white" }} onChange={(e)=> setSize(e.target.value)} >
                           {priceOptions.map((data)=>{
                            return <option key={data} value={data}>{data}</option>
                           })}
                        </select>
                        <div className='d-inline h-100 fs-5' style={{ color: "black" }}>
                            Rs{finalPrice}/-
                        </div>
                    </div>
                    <hr />
                    <button className={`btn btn-success justify-center ms-2`} onClick={handaleAddToCart}>Add to Cart</button>
                </div>
            </div>
        </div>
    )
}

export default Card
