import React, { useEffect, useState } from 'react';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function MyOrder() {
    const [orderData, setOrderData] = useState([]);

    const fetchMyOrder = async () => {
        try {
            const response = await fetch("http://localhost:5000/api/myorderData", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: localStorage.getItem('userEmail')
                })
            });
            const result = await response.json();
            // Check if orderData is an array, if not, convert it to an array
            const orderDataArray = Array.isArray(result.orderData.order_data[0][1])
                ? result.orderData.order_data[0][1]
                : [result.orderData.order_data[0][1]];
            setOrderData(orderDataArray);
        } catch (error) {
            console.error('Error fetching my order:', error);
        }
    };


    useEffect(() => {
        fetchMyOrder();
    }, []);
console.log(orderData)
    return (
        <div>
            <Navbar />
            <div className='container'>
                <div className='row'>
                    {orderData ? orderData.map((arrayData, index) => (
                        <div className='col-12 col-md-6 col-lg-3' key={index}>
                            <div className="card mt-3" style={{ width: "16rem", maxHeight: "360px" }}>
                                <img src={arrayData.img} className="card-img-top" alt="..." style={{ height: "120px", objectFit: "fill" }} />
                                <div className="card-body">
                                    <h5 className="card-title">{arrayData.name}</h5>
                                    <div className='container w-100 p-0' style={{ height: "38px" }}>
                                        <span className='m-1' style={{color:black}}>{arrayData.qty}</span>
                                        <span className='m-1'>{arrayData.size}</span>
                                        <span className='m-1'>{arrayData.date}</span>
                                        <div className=' d-inline ms-2 h-100 w-20 fs-5' >
                                            â‚¹{arrayData.price}/-
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )) : null}
                </div>
            </div>
            <Footer />
        </div>
    );
}
